		let ang = this.speed.heading()
		push()
			imagemode(CENTER)
			translate(this.p.x,this.p,y)
			rotate(ang)
		pop()
	}

  update() {

		//flashLight expell
		this.p.add(this.speed)
		let d = dist(mouseX,mouseY,this.p.x,this.p.y)
		if (d<100){
			this.speed = p5.Vector.random2D().mult(3)
			this.size += 0.05 
		}

    // Update position based on noise
    this.p.x += map(noise(this.xOff), 0, 1, -5, 5);
    this.p.y += map(noise(this.yOff), 0, 1, -5, 5);

  }

  show(){
		noStroke();
		fill(this.color);
    circle(this.p.x, this.p.y, this.size);
  }
}

