let cubes = [];

function setup() {
  createCanvas(500, 400, WEBGL);
  let offset = 100;
  let spacing = 110;

  for (let x = -1; x <= 1; x++) {
    for (let y = -1; y <= 1; y++) {
      for (let z = -1; z <= 1; z++) {
        let cube = new Cube(x * spacing, y * spacing, z * spacing, offset);
        cubes.push(cube);
      }
    }
  }
}

function draw() {
  background(255);
  // Display cubes
  for (let cube of cubes) {
    cube.rotate();
    cube.display();
  }
}

class Cube {
  constructor(x, y, z, offset) {
    this.x = x;
    this.y = y;
    this.z = z;
    this.offset = offset;
    this.angleX = 0;
    this.angleY = 0;
    this.angleZ = 0;
    this.noiseX = random(10);
    this.noiseY = random(10);
    this.noiseZ = random(10);


    this.size = random(50, 300); 
    this.opacity = random(10, 200); 
  }

  rotate() {
    let noiseFactor = 0.05; 
    this.angleX += noise(this.noiseX) * noiseFactor;
    this.angleY += noise(this.noiseY) * noiseFactor;
    this.angleZ += noise(this.noiseZ) * noiseFactor;

    this.noiseX += 0.01;
    this.noiseY += 0.01;
    this.noiseZ += 0.01;
  }

  display() {
    push();
    translate(this.x, this.y, this.z);
    rotateX(this.angleX);
    rotateY(this.angleY);
    rotateZ(this.angleZ);

    // Apply color, size, and opacity
    fill('pink');
    stroke(1);
    strokeWeight(1);
    ambientMaterial(300);
    shininess(50);
    specularMaterial((random(255)));
    shininess(50);
    ambientLight(230);
    pointLight(255, 255, 0, 0, 0, 255);

    box(this.size);
    pop();
  }
}

