function drawBush(x, y, length) {
  myTurtle.penUp();
  myTurtle.moveTo(x, y);
  myTurtle.turnTo(-90);
  myTurtle.penDown();
  drawBranch(length);
}
function drawBranch(length) {
  changeColor(length);
  if (length < 3) {
    return;
  }
  // draw this branch
  myTurtle.moveForward(length);

  // left child
  myTurtle.pushState();
  changeColor(length);
  myTurtle.turnLeft(50);
  drawBranch(length * 0.79);
  myTurtle.popState();

  // right child
  myTurtle.pushState();
  changeColor(length);
  myTurtle.turnRight(50);
  drawBranch(length * 0.79);
  myTurtle.popState();
}

function changeColor(length) {
  if (length < 20) {
    var amt = map(log(length), 1, 3, 0, 1);
    var c4 = lerpColor(c2, c3, amt);
    stroke(c4);
  } else {
    stroke(c1);
  }
}

function drawFlower(x,y) {
    let startColor = color("#c45d66"); 
    let endColor = color("#e2b5b8"); 
    myFlower.penUp();
    myFlower.moveTo(x,y);
    myFlower.pushState();
    drawStem();
    myFlower.popState();
    myFlower.penDown();

    for (i=0; i<10; i++){
        let colorAmount = map(i, 0,10, 0, 1);
        let currentColor = lerpColor(startColor, endColor, colorAmount);
        stroke(currentColor);
        strokeWeight(2);
        drawCircle(50-i)
        myFlower.turnLeft(100);
        drawCircle(50-i);
        myFlower.turnLeft(100);
    }
}

function drawCircle(radius) {
  const angle = 360 / 50;
  for (let i = 0; i < 50; i++) {
    myFlower.moveForward(0.6 * radius * sin(angle / 1.5));
    myFlower.turnRight(angle);
  }
}

function drawStem(){
     myFlower.penUp();
     stroke('#001900');
     strokeWeight(5);
     myFlower.turnTo(90);
     myFlower.penDown();
     myFlower.moveForward(200);
     myFlower.penUp();
}

