class Balls{
  constructor(x, y, size, velocity, r, g, b){
    this.x = x;
    this.y = y;
    this.size = size;
    this.velocity = velocity;
    this.r = r;
    this.g = g;
    this.b = b;

    this.gravity = 0.3;
    this.friction = 0.9;

  }

  Color(){
    fill(this.r, this.g, this.b);
  }

  physics(){
    this.velocity += this.gravity;
    this.y = this.y + this.velocity;

    if (this.y > height) {
      this.y = height;
      this.velocity *= -1 * this.friction;
    }
  }

  display(){
    ellipse(this.x, this.y, this.size);
  }
}

