var params;
var flowers = [];

function setup() {
  createCanvas(400, 400);
  params = {
    background_color: "#fff",
    l1: 100,
    l2: 50,
    speed1: 1,
    speed2: 5,
    stroke_weight: 10,
    line_color: "#000",
  };

  const pane = new Tweakpane.Pane();
  pane.addInput(params, "background_color");
  pane.addInput(params, "l1", { min: 50, max: 150 });
  pane.addInput(params, "l2", { min: 25, max: 100 });
  pane.addInput(params, "speed1", { min: 0.25, max: 1.5 });
  pane.addInput(params, "speed2", { min: 3, max: 6 });
  pane.addInput(params, "stroke_weight", { min: 1, max: 20 });
  pane.addInput(params, "line_color");

  flowers.push({ x: width / 2, y: height / 2, theta: 0, theta2: 0 }); // Center
  flowers.push({ x: width / 2, y: 0, theta: 0, theta2: 0 }); // Top
  flowers.push({ x: 0, y: height / 2, theta: 0, theta2: 0 }); // Left
  flowers.push({ x: width, y: height / 2, theta: 0, theta2: 0 }); // Right
  flowers.push({ x: width / 2, y: height, theta: 0, theta2: 0 }); // Bottom
}

function draw() {
  if (mouseIsPressed === true) {
    for (let flower of flowers) {
      drawClock(flower);
    }
    background(0, 0, 0, 0);
  } else {
    background(params.background_color);
  }
}

function drawClock(flower) {
  let localX1 = (params.l1) * sin(flower.theta);
  let localY1 = (params.l1) * cos(flower.theta);
  let localX2 = (params.l2) * sin(flower.theta2);
  let localY2 = (params.l2) * cos(flower.theta2);

  push();
  stroke(params.line_color);
  translate(flower.x, flower.y);
  line(0, 0, localX1, localY1);
  translate(localX1, localY1);
  line(0, 0, localX2, localY2);
  strokeWeight(params.stroke_weight);
  point(localX2, localY2);
  pop();
  flower.theta += radians(params.speed1);
  flower.theta2 += radians(params.speed2);
}

