    this.y = random(-height, height);
    this.size = random(0.5, 3);
    // Noise offset for smoother transition
    this.xOff = random(0, 1000);
    this.yOff = random(0, 1000);
  }

  update() {
    // Update position based on noise
    this.x += map(noise(this.xOff), 0, 1, -5, 5);
    this.y += map(noise(this.yOff), 0, 1, -5, 5);

    // Increment noise offset
    this.xOff += speed;
    this.yOff += speed;

    // Wrap around edges
    if (this.x < -width / 2) {
      this.x = width / 2;
    } else if (this.x > width / 2) {
      this.x = -width / 2;
    }
    if (this.y < -height / 2) {
      this.y = height / 2;
    } else if (this.y > height / 2) {
      this.y = -height / 2;
    }
  }

  show() {
    noStroke();
    fill(255);
    circle(this.x, this.y, this.size);
  }
}

let myTurtle;

function setup() {
  createCanvas(1000, 1000);
  noFill();
  background(255);
  noLoop();
  angleMode(DEGREES);
  myTurtle = new Turtle();
}

function draw() {
  background(20); // Dark background to represent soil
  for (let i = 0; i < 10; i++) { // Draw 5 trees to create a small forest
    myTurtle.penUp();
    // Randomize the starting position
    myTurtle.moveTo(random(width), 800);
    myTurtle.turnTo(-90);
    myTurtle.penDown();
    drawBranch(random(50, 180)); // Randomize the size of the trees


    myTurtle.moveTo(1000-random(width), 200);
    drawBranch(random(25, 80)); // Randomize the size of the trees

  }
}

function drawBranch(length) {
  if (length < 5) {
    // Instead of stopping, draw a rotation triangle
    drawRotationTriangle();
    return;
  }
  // draw this branch
  myTurtle.moveForward(length);

  // left child
  myTurtle.pushState();
  myTurtle.turnLeft(60);
  drawBranch(length * 0.6);
  myTurtle.popState();


  // right child
  myTurtle.pushState();
  myTurtle.turnRight(35);
  drawBranch(length * 0.8);
  stroke(180, 180, 180); // Soft pink outline
  myTurtle.popState();
}

function drawRotationTriangle() {
  // Set the color for the rotation triangle
  stroke(220, 140, 190); // Soft pink outline

  fill(220, 140, 190);
  myTurtle.pushState();
  for (let i = 0; i < 50; i++) { // Draw a triangle
    myTurtle.moveForward(1);
    myTurtle.turnRight(5);
  }
  myTurtle.popState();
}

