       stroke(r, g1, r1);
     } else if(x%3 == 2){
       stroke(b1, b, g1);
     }
  }

  for(let y=0; y<=width; y+=spacing){
    line(width, y, i, j);
    if(y%3 == 0){
       stroke(r1, g1, b1);
     } else if(y%3 == 1){
