let fireworks = []; // Array to store information about each firework

function setup() {
  createCanvas(700, 700);

}

function draw() {
  background(10, 20);

  // Create new fireworks gradually
  if (frameCount % 20 === 0) {
    fireworks.push({
      x: random(width), // Random starting x-coordinate
      y: random(height), // Random starting y-coordinate
      numLines: random(10,40), // Number of lines in each firework
      length: random(10, 50), // Initial length of each line
      angleOffset: random(TWO_PI) // Random initial angle offset
    });
  }

  // Update and draw existing fireworks
  for (let i = 0; i < fireworks.length; i++) {
    let firework = fireworks[i];
    let speed = 2; // Speed of line expansion

    // Draw and update each line
    for (let j = 0; j < firework.numLines; j++) {
      let angle = j * TWO_PI / firework.numLines; // Angle of this line
      let lineLength = firework.length * (frameCount / 100); // Adjust line length based on frame count

      let newX = firework.x + cos(angle + firework.angleOffset) * lineLength;
      let newY = firework.y + sin(angle + firework.angleOffset) * lineLength;

      strokeWeight(2);
      stroke(random(255),random(255),random(255), 100);
      point(newX, newY);
    }

    // Remove fireworks
    if (firework.length * (frameCount / 100) > max(100, 100)) {
      fireworks.splice(i, 1);
    }
  }
}

