  let frequency = frequency_slider.value();
  let time_speed = time_speed_slider.value() / 20;

  noiseDetail(1, 0.5);

  fill(255);
  noStroke();

  // Study this loop. Do you understand how the line of ellipses is created?
  for (let i = 0; i < 1; i += 0.02) {
    let t = time * time_speed + i; 

    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    // Hint: Drive offsetX and offsetY with noise() instead of random()
    let offsetX = (noise(t * frequency) - 0.5) * amplitude * 10; 
    let offsetY = (noise(t * frequency) - 0.5) * amplitude * 10; 

    ellipse(x + offsetX, y + offsetY, 10, 10);
  }

  time += 0.01; 
}

let cols, rows;
let scale = 20; // This determines the spacing between the circles

let colorStart;
let colorEnd;

function setup() {
  createCanvas(400, 400);
  cols = width;
  rows = height;
  colorMode(HSB, 360, 100, 100);
   // Create UI sliders
  createP('Scale');
  scaleSlider = createSlider(10, 100, 20); // Min, max, default values
  createP('Color Start (Hue)');
  colorStartSlider = createSlider(0, 360, 0); // Hue from 0 to 360
  createP('Color End (Hue)');
  colorEndSlider = createSlider(0, 360, 360); // Hue from 0 to 360
}


function draw() {
  background(0);

  // Determines the noise density
  let noiseScale = 0.05;

   // Update scale based on slider value
  scale = scaleSlider.value();
  cols = width / scale;
  rows = height / scale;

  // Get color values from sliders
  let hueStart = colorStartSlider.value();
  let hueEnd = colorEndSlider.value();
  let colorStart = color(hueStart, 100, 100); // Full saturation and brightness
  let colorEnd = color(hueEnd, 100, 100);

 for (let x = 0; x < cols; x++) {
    for (let y = 0; y < rows; y++) {
      // Calculate noise value based on grid position
      let noiseVal = noise(x * noiseScale, y * noiseScale);

      // Map noise value to circle size, with a minimum size threshold
      let circleSize = map(noiseVal, 0, 1, 2, scale * 0.8);

      let lerpedColor = lerpColor(colorStart, colorEnd, noiseVal);


      noFill();
      stroke(lerpedColor);
      ellipse(x * scale, y * scale, circleSize, circleSize);
    }
  }
}

