  background(255);
  image(imageTest, 0, 0, width, height);

  for (let y = 0; y < height; y += pixelSize) {
    for (let x = 0; x < width; x += pixelSize) {
      // calculate the position in the image
      const i =
        (floor((y * imageTest.height) / height) * imageTest.width +
          floor((x * imageTest.width) / width)) *
        4;
      const r = imageTest.pixels[i];
      const g = imageTest.pixels[i + 1];
      const b = imageTest.pixels[i + 2];
      // draw ellipse with the color of the image pixel
      fill(r, g, b);
      ellipse(x + pixelSize * 4, y + pixelSize / 2, pixelSize, pixelSize);
    }
  }
}

