let diceFaceSize = 100; // Size of each dice face
let diceMargin = 20; // Margin between dice faces
let totalFaces = 6; // Total number of faces on each dice
let currentFace1 = 1; // Variable to store the current face shown on dice 1
let currentFace2 = 1; // Variable to store the current face shown on dice 2
let chosenOption = 0; // Variable to store the chosen option by the player
let rollingDice = false; // Variable to indicate whether the dice are rolling
let options = []; // Array to store random options

function setup() {
  createCanvas(400, 400);
  generateRandomOptions();
}

function draw() {
  background(220);

  // Display "Guess your number" text
  textSize(20);
  fill(0); // Set the fill color to black for the text
  textAlign(CENTER, CENTER);
  text("Guess your number", width / 2, 30); // Position the text at the top center

  // Display random options
  textSize(24);
  options.forEach((option, index) => {
    let xPosition = (index + 1) * width / (options.length + 1);
    text(option, xPosition, height / 2);
  });

  // Display dice faces
  displayDiceFace(currentFace1, width / 4, height - 100);
  displayDiceFace(currentFace2, 3 * width / 4, height - 100);

  // Check if an option has been chosen and the dice are not rolling
if (!rollingDice && chosenOption !== 0) { // Make sure the dice roll has finished and an option was chosen
    let sum = currentFace1 + currentFace2;
    textSize(32);
    textAlign(CENTER, CENTER);
    text("Sum: " + sum, width / 2, height / 2 + 40); // Confirm this is visible

    // Display win/lose message
    if (options.includes(sum)) {
      fill(0, 255, 0); // Green for win
      text("Win!", width / 2, height / 2 + 80); // Confirm this is visible
    } else {
      fill(255, 0, 0); // Red for lose
      text("Lose", width / 2, height / 2 + 80); // Confirm this is visible
    }

  }
}
function mouseClicked() {
  if (!rollingDice) {
    for (let index = 0; index < options.length; index++) {
      let option = options[index];
      let optionX = (index + 1) * width / (options.length + 1);
      if (mouseX > optionX - 50 && mouseX < optionX + 50 && mouseY > height / 2 - 30 && mouseY < height / 2 + 30) {
        chosenOption = option;
        rollDice();
        return; // Use 'return' instead of 'break' to exit the function
      }
    }
  }
}

function rollDice() {
  // Choose a random sum from the available options
  let sumIndex = floor(random(0, options.length));
  let targetSum = options[sumIndex];

  rollingDice = true;
  setTimeout(() => {
    // Randomly choose new dice faces until the target sum is reached
    do {
      currentFace1 = floor(random(1, totalFaces + 1));
      currentFace2 = floor(random(1, totalFaces + 1));
    } while (currentFace1 + currentFace2 !== targetSum);

    rollingDice = false;
    // Reset chosenOption to 0 to wait for the next selection
    chosenOption = 0; 
    // Generate new options for the next selection
    generateRandomOptions();
  }, 1000);
}

function displayDiceFace(face, x, y) {
  // Draw the outline of the dice
  rectMode(CENTER);
  fill(255);
  stroke(0);
  rect(x, y, diceFaceSize, diceFaceSize, 10);

  // Draw the dots based on the face value
  fill(0);
  noStroke();
  let dotSize = 10;
  let spacing = 20;
  let startX = x - spacing;
  let startY = y - spacing;
  let endX = x + spacing;
  let endY = y + spacing;

  // Display dots based on the face value
if (face === 1 || face === 3 || face === 5) { // Corrected 'or' to '||'
  ellipse(x, y, dotSize, dotSize); // Center dot
}
if (face >= 2) {
  ellipse(startX, startY, dotSize, dotSize); // Top left dot
  ellipse(endX, endY, dotSize, dotSize); // Bottom right dot
}
if (face >= 4) {
  ellipse(endX, startY, dotSize, dotSize); // Top right dot
  ellipse(startX, endY, dotSize, dotSize); // Bottom left dot
}
if (face === 6) {
  ellipse(startX, y, dotSize, dotSize); // Middle left dot
  ellipse(endX, y, dotSize, dotSize); // Middle right dot
}
	}


function generateRandomOptions() {
  options = []; // Clear previous options
  while (options.length < 3) {
    let randomOption = floor(random(1, totalFaces * 2 + 1)); // Generate random options within the possible sum range (2-12)
    if (!options.includes(randomOption)) {
      options.push(randomOption);
    }
  }
}

