  fill(100);
  noStroke();
  rectMode(CENTER);

}

function draw() {
  background(0);

  //Squares Grid
  for (let i=0; i<50; i++){
    for (let j=0; j<50; j++){
      let nx = params.noiseScale * i;
      let ny = params.noiseScale * j;
      let nt = params.noiseScale * frameCount/2;

      // Compute noise value.
      let r = params.noiseLevel * noise(nx, ny, nt);
      let b = params.b * params.noiseLevel * noise(nx, ny, nt)/100;
      // Render.
      noStroke();
      fill(r, params.g, b, r);

      rect(i*10+10, j*10+10, params.size, params.size, params.corner);
    }
  }



  for (let i=0; i<50; i++){
    for (let j=0; j<50; j++){
      let nx = params.noiseScale * i;
      let ny = params.noiseScale * j;
      let nt = params.noiseScale * frameCount;

      // Compute noise value.
      let r = params.noiseLevel * noise(nx, ny, nt);
      let b = params.b * params.noiseLevel * noise(nx, ny, nt)/100;
      // Render.
      noFill();
      stroke(b-50, params.g-50, r-50, params.b+100);

      rect(i*10+12, j*10+12, params.size, params.size, params.corner);
    }
  }
}

