let cols, rows;
let scale = 3;// Size of each cell in the grid (aka what I'm dividing the sketch by lengthwise & heightwise)
let terrain = [];// Array to store terrain type (snow, water, earth, rock)

let points = [];//Array to store coordinates
let departure, treasure;//pos of dep & treasure

function setup() {
  createCanvas(400, 400);
  cols = width / scale;
  rows = height / scale;

  //Generate 2 x,y coordinates, one treasure and one departure point
  generateRandomPoints(2);

  //Generate terrain data using Perlin noise
  generateMap();

  //Draw the terrain
  drawMap();

  //Draw origin point and treasure
  drawRects();

  //Draw line between the 2
  drawLines();
}

function generateMap() {
  //Loop through each row and column
  for (let i = 0; i < cols; i++) {
    terrain[i] = [];
    for (let j = 0; j < rows; j++) {
      //Calculate Perlin noise value for current grid cell
      let xoff = map(i, 0, cols, 0, 2);
      let yoff = map(j, 0, rows, 0, 2);
      let noiseVal = noise(xoff, yoff);

      //Terrain type based on noise value
      if (noiseVal < 0.4) {
        terrain[i][j] = "water"; // Water
      } else if (noiseVal < 0.6) {
        terrain[i][j] = "earth"; // Earth
      } else if (noiseVal < 0.8) {
        terrain[i][j] = "rock"; // Rock
      } else {
        terrain[i][j] = "snow"; // Snow
      }
    }
  }
}

function drawMap() {
  //Draw each cell with relevant color
  for (let i = 0; i < cols; i++) {
    for (let j = 0; j < rows; j++) {
      let colorVal;
      switch (terrain[i][j]) {
        case "earth":
          colorVal = color(0, 255, 0); // Green for earth
          break;
        case "water":
          colorVal = color(0, 0, 255); // Blue for water
          break;
        case "rock":
          colorVal = color(128); // Grey for rock
          break;
        case "snow":
          colorVal = color(255); // White for snow
          break;
      }
      fill(colorVal);
      noStroke();
      rect(i * scale, j * scale, scale, scale);
    }
  }
}

function generateRandomPoints(numPoints) {
  for (let i = 0; i < numPoints; i++) {
    let x = random(50,350);
    let y = random(50,350);
    points.push({ x, y });
  }
}

function drawRects() {
  for (let i = 0; i < points.length; i++) {
    let point = points[i];
    let point1 = points[(i + 1) % points.length];

    push();
    fill(0);
    drawRect(point.x, point.y); //Draw black rect for departure point
    pop();

    if (i === 1) { //Check if this is the treasure point, if so, write "treasure"
      push();
      fill(255, 0, 0); 
      drawRect(point1.x, point1.y); //Draw red rect for treasure point
      textAlign(CENTER, CENTER);
      textSize(14);
      strokeWeight(2);
      stroke(255);
      fill(0); 
      text("Treasure!", point1.x + 4, point1.y + 15); // Adjust text position
      pop();

    }
  }
}


function drawRect(x, y) {
  noStroke(); 
  rect(x, y, 8, 8); 
}

function drawLines(){
  for (let i = 0; i < points.length; i++) {
    stroke(0); 
    let departure = points[i]; //first point
    let treasure = points[(i + 1) % points.length]; //second point
  line(departure.x, departure.y, treasure.x, treasure.y);
  line(departure.x,departure.y, treasure.x, treasure.y);
  }
}

