let player;
let obstacles = [];
let backgroundImage;
let newBackgroundImage; 
let gameOverImage;
let score = 0;
let timeElapsed = 0;
let gameStarted = false; 

function preload() {
  backgroundImage = loadImage('startbutton.jpeg'); 
  newBackgroundImage = loadImage('Designer.jpg'); 
  gameOverImage = loadImage('gameover.jpg'); 
}

function setup() {
  createCanvas(550, 550);
  player = new Player();
}

function draw() {
  if (gameStarted) { 
    background(newBackgroundImage);

    player.update();
    player.show();

    if (frameCount % 120 === 0) { 
      obstacles.push(new Obstacle());
    }

    for (let i = obstacles.length - 1; i >= 0; i--) {
      obstacles[i].update();
      obstacles[i].show();

      if (player.hits(obstacles[i])) {
        gameOver();
        return; 
      }

      if (obstacles[i].offscreen()) {
        obstacles.splice(i, 1);
        score++;
      }
    }

    textSize(20);
    fill(255);
    stroke('black');
    strokeWeight(5);
    text(`Score: ${score}`, 10, 30);
    text(`Time Elapsed: ${timeElapsed}`, 10, 60);
  } else {
    background(backgroundImage);
  }
}

function mousePressed() {
  if (!gameStarted) {
    gameStarted = true;
    setInterval(updateTime, 1000); 
  }
}

function updateTime() {
  timeElapsed++;
}

function gameOver() {
  noLoop();
  background(gameOverImage); 
  obstacles = []; 
}

function keyPressed() {
  if (keyCode === UP_ARROW) {
    player.move(-1);
  } else if (keyCode === DOWN_ARROW) {
    player.move(1);
  }
}

class Player {
  constructor() {
    this.y = height / 2;
    this.velocity = 0;
    this.gravity = 0.6;
  }

  move(dir) {
    this.velocity += dir * 5;
  }

  update() {
    this.velocity *= 0.9;
    this.y += this.velocity;
    this.y = constrain(this.y, 0, height - 50);
  }

  show() {
    fill('orange');
    rect(50, this.y, 20, 50);
  }

  hits(obstacle) {
    return (
      this.y < obstacle.y + obstacle.height &&
      this.y + 50 > obstacle.y &&
      50 + 20 > obstacle.x &&
      50 < obstacle.x + obstacle.width
    );
  }
}

class Obstacle {
  constructor() {
    this.x = width;
    this.y = random(height);
    this.width = 20;
    this.height = 50;
    this.speed = 5;
  }

  update() {
    this.x -= this.speed;
  }

  show() {
    fill('white');
    rect(this.x, this.y, this.width, this.height);
  }

  offscreen() {
    return this.x < -this.width;
  }
}

