function drawIris(){

  for (let i = 0; i < numLines; i++) {
    let angle = map(i, 0, numLines, 0, TWO_PI);
    let newAngle = noise(angle*0.1)
    let endX = x + radius * cos(angle);
    let endY = y +radius * sin(angle);
    radius+= ((noise(radius) - 0.45) * 20);
    strokeWeight(2);
    line(x, y, endX, endY);
  }

}

function setup() {
  createCanvas(400, 400);
}

function draw() {
  background(50);

  noStroke();
  ellipseMode(CENTER);

  let noiseFrequency = 0.50;

  for (let i = 0; i < 50; i++) {
    let x = noise(i * noiseFrequency) * width;
    let y = noise(100 + i * noiseFrequency) * height;

    let diameter = noise(200 + i * noiseFrequency) * 20 + 5; // vary diameter
    let hue = noise(300 + i * noiseFrequency) * 255; // hue value

    colorMode(HSB, 255); //HSB color mode

    fill(hue, 255, 255); //full saturation and brightness  hue

    ellipse(x, y, diameter, diameter);
  }

  noLoop();
}

