function setup() {
 // Create a canvas that is the same size as the image
  createCanvas(img.width, img.height);
  // Resize the image to half its original width and height
  img.resize(img.width / 2, img.height / 2);
  // Adjust the canvas size to match the new image size
  resizeCanvas(img.width, img.height);
  pixelDensity(1);
  img.loadPixels();
  noLoop(); // Draw only once
}

function draw() {
  // Display the original image
  image(img, 0, 0);

  // Draw the grass on top of the image
  for (let x = 0; x < img.width; x++) {
    for (let y = 0; y < img.height; y++) {
      // Calculate the index for the pixels array from x and y
      let index = (x + y * img.width) * 4;
      // Extract the pixel's color
      let r = img.pixels[index];
      let g = img.pixels[index + 1];
      let b = img.pixels[index + 2];
      // Calculate brightness
      let brightness = (r + g + b) / 3;
      // Determine the size of the grass blade based on brightness
      let grassSize = map(brightness, 0, 255, 2, 12);
      // Set the stroke color to the color of the pixel
      stroke(r, g, b);
      // Draw the grass blade
      drawGrassBlade(x, y, grassSize);
    }
  }
}

function drawGrassBlade(x, y, size) {
  let angle = random(TWO_PI); // Random angle for the grass blade
  let x2 = x + cos(angle) * size * 5;
  let y2 = y + sin(angle) * size * 5;
  line(x, y, x2, y2);
}

