let time = 0; 
let dots = [];

function setup() {
  createCanvas(500, 300);

  createP("Amplitude");
  amplitude_slider = createSlider(0, 100, 50);

  createP("Frequency");
  frequency_slider = createSlider(0, 1, 0.5, 0.01);

  for (let i = 0; i < 50; i++) {
    dots.push({
      x: 0,
      y: 0,
      offset: i * 0.1, 
    });
  }
}

function draw() {
  background(50);
  ellipseMode(CENTER);

  let amplitude = amplitude_slider.value() / 100;
  let frequency = frequency_slider.value();

  fill(255);
  noStroke();

  for (let i = 0; i < dots.length; i++) {
    let dot = dots[i];

    let dotSpacing = (endX - startX) / dots.length;
    let x = mean(startX, endX, i / dots.length);
    let y = mean(startY, endY, i / dots.length);

    let offsetY = sin(time + dot.offset) * amplitude * 10;

    ellipse(x, y + offsetY, 10, 10);
  }

  time += frequency;
}

function mean(start, end, time) {
  return start * (1 - time) + end * time;
}

