let noiseLevel = 220;
let i = 0; // Global variable to keep track of the current index in randomValues

let randomValues = [50,60,105,303,210, 39];

function setup() {
  createCanvas(600, 600);
  frameRate(60);
  ellipseMode(CENTER);
}

function draw() {
  //background(noiseID(i+1), noiseID(i+3), noiseID(i+2));
  background(240);
  //stroke(220);
  i+=0.01;
  strokeWeight(4);



  stroke(noiseID(i), noiseID(i+1), noiseID(i+2));

  //drawCircle();

  push();
  blendMode(MULTIPLY);
  circle_1();
  pop();
  push();
  blendMode(MULTIPLY);
  stroke(noiseID(i+1), noiseID(i+2), noiseID(i+1));
  translate(200,200);
  circle_1();
  pop();
  push();
  blendMode(MULTIPLY);
  stroke(noiseID(i+2), noiseID(i+2), noiseID(i+1));
  translate(200,-200);
  circle_1();
  pop();
  push();
  blendMode(MULTIPLY);
  stroke(noiseID(i), noiseID(i), noiseID(i+1));
  translate(-200,-200);
  circle_1();
  pop();
  push();
  blendMode(MULTIPLY);
  stroke(noiseID(i+3), noiseID(i+4), noiseID(i));
  translate(-200,200);
  circle_1();
  pop();
}

function circle_1() {
// Compute noise value.
    noiseDetail(8, 0.025);
    let c = noiseLevel * noise(0.02, frameCount * 0.001);


  const steps = 20;
  const centerX = width / 2;
  const centerY = height / 2;
  //const radius = 200 + c*5;
  const radius = noiseID(i);
  for (let step = 1; step <= steps; step++) {
    const a1 = map(step - 1, 0, steps, 0, 2 * PI*c);
    const a2 = map(step, 0, steps, 0, 2 * PI*c);
    //const x1 = centerX + sin(a1) * radius+c/3; for circle 2
    const x1 = centerX + sin(a1) * radius;
    const x2 = centerX + sin(a2) * radius;
    const y1 = centerY + cos(a1) * radius;
    const y2 = centerY + cos(a2) * radius;
    line(x1, y1, x2, y2);
  }
}

//lerp gives you a number between 2 other numbers. E.g. lerp (10,20,.5) means you'll get a number 50% way through 10 and 20.
function noiseID(i = 0){
  let low = randomValues[floor(i) % randomValues.length];
  let high = randomValues[ceil(i) % randomValues.length];

  return lerp(low, high, fract(i));
}

