module right_triangle(side1, side2, corner_radius, triangle_height) {
    // Calculate the center position
    center_x = side1 / 2;
    center_y = side2 / 7;

    translate([-center_x, -center_y, 0]) {
        hull() {
            cylinder(r=corner_radius, h=triangle_height);
            translate([side1 - corner_radius * 0, 0, 0]) cylinder(r=corner_radius, h=triangle_height);
            translate([2, side2 - corner_radius * 0, 0]) cylinder(r=corner_radius, h=triangle_height);
        }
    }
}

