circle_radius_3 = 2;

module pillar(width, depth, height) {
    color([1,1,1]) cube([width, depth, height], center=false);
}

module pillars() {
    // Level 1
    translate([0, 0, 0]) {
        for (i = [0:pillar_count_1-1]) {
            rotate([0, 0, i * 360/pillar_count_1])
            translate([circle_radius_1, 0, 0])
            pillar(pillar_width_1, pillar_depth_1, pillar_height_1);
        }
    }

    // Level 2
    translate([0, 0, pillar_height_1]) {
        for (i = [0:pillar_count_2-1]) {
            rotate([0, 0, i * 360/pillar_count_2])
            translate([circle_radius_2, 0, 0])
            pillar(pillar_width_2, pillar_depth_2, pillar_height_2);
        }
    }

    // Level 3
    translate([0, 0, pillar_height_1 + pillar_height_2]) {
        for (i = [0:pillar_count_3-1]) {
            rotate([0, 0, i * 360/pillar_count_3])
            translate([circle_radius_3, 0, 0])
            pillar(pillar_width_3, pillar_depth_3, pillar_height_3);
        }
    }
}

