// Wait for the DOM content to be fully loaded
document.addEventListener("DOMContentLoaded", function() {
  // Get a reference to the canvas object
  const canvas = document.getElementById("myCanvas");

  // Set the background color of the body element
  document.body.style.backgroundColor = "#5D98F9"; // Example color: light gray

  // Initialize Paper.js on the canvas
  paper.setup(canvas);

  // Create a tool for drawing
  const drawingTool = new paper.Tool();

  // Set default stroke color
  let strokeColor = "white";

  // When the mouse is dragged, draw an ellipse
  drawingTool.onMouseDrag = function(event) {
    // Create an ellipse
    const ellipse = new paper.Path.Ellipse({
      center: event.downPoint,
      radius: event.delta.multiply(6), // Adjust the radius based on mouse movement
      strokeColor: strokeColor,
      strokeWidth: 1 // Set the stroke width
    });
  };

  // Function to change stroke color
  function changeStrokeColor(color) {
    strokeColor = color;
  }

  // Function to clear the canvas
  function clearCanvas() {
    paper.project.activeLayer.removeChildren();
  }

  // Function to export the canvas as an SVG
  function exportCanvas() {
    // use default name if not provided
    const fileName = "output.svg";

    // create a data url of the file
    const svgData = paper.project.exportSVG({ asString: true });
    const url = "data:image/svg+xml;utf8," + encodeURIComponent(svgData);

    // create a link to the data, and "click" it
    const link = document.createElement("a");
    link.download = fileName;
    link.href = url;
    link.click();
  }

  // Add event listener for keydown event
  document.addEventListener("keydown", function(event) {
    if (event.key === "s") {
      exportCanvas();
    }
  });

  // Add event listeners to HTML buttons if they exist
  const clearButton = document.getElementById("clearButton");
  const exportButton = document.getElementById("exportButton");

  if (clearButton && exportButton) {
    clearButton.addEventListener("click", clearCanvas);
    exportButton.addEventListener("click", exportCanvas);
  }
});

$fn = 20; // Define the resolution for circle-based primitives.

// Brick effect module as provided.
module brick_effect(size, height, color) {
    for (x = [0:size[0]/0.4 - 1]) {
        for (y = [0:size[1]/0.4 - 1]) {
            if ((x + y) % 2 == 0) {
                color(color)
                translate([x * 0.4 - size[0]/2, y * 0.4 - size[1]/2, height/2 - 0.1])
                    cube([0.4, 0.4, 0.2], true);
            }
        }
    }
}

// Module to create a tower with crenellations.
module tower(baseWidth, height, roofHeight, towerColor, roofColor) {
    // Create the tower base with the brick effect.
    translate([0, 0, height / 2]) {
        color(towerColor) // Set tower color
        cube([baseWidth, baseWidth, height], center = true);
        brick_effect([baseWidth, baseWidth], height, towerColor);
    }
    // Create crenellations.
    for(i = [0 : 360 / $fn : 360]) {
        if (i % 2 == 0) { // Adjust this for the desired number of crenellations.
            color(towerColor) // Set tower color
            translate([baseWidth / 2 * cos(i), baseWidth / 2 * sin(i), height]) {
                cube([baseWidth / 5, baseWidth / 5, 0.5], center = true);
            }
        }
    }
    // Add the conical roof.
    translate([0, 0, height + roofHeight / 2]) {
        color(roofColor) // Set roof color
        rotate([180, 0, 0]) {
            cylinder(h = roofHeight, r1 = 0, r2 = baseWidth / 2, center = true);
        }
    }
}

// Create the main gate section with an arch and brick effect.
module gate(width, height, thickness, gateColor) {
    difference() {
        translate([0, 0, height / 2]) {
            color(gateColor) // Set gate color
            cube([width, thickness, height], center = true);
            brick_effect([width, thickness], height, gateColor);
        }
        // Subtract the arch shape for the door.
        translate([0, 0, height / 4]) {
            scale([width / 4, 1, height / 2]) {
                cylinder(h = thickness + 0.1, r = 1, center = true);
            }
        }
    }
}

// Define colors.
towerColor = [0.59, 0.29, 0]; // Brown color for the tower.
roofColor = [1, 0, 0]; // Red color for the roof.
gateColor = [0.59, 0.29, 0]; // Brown color for the gate.

// Main castle structure using the modules.
union() {
    // Left tower.
    translate([-6, 0, 0]) {
        tower(3, 8, 5, towerColor, roofColor);
    }
    // Right tower.
    translate([6, 0, 0]) {
        tower(3, 8, 5, towerColor, roofColor);
    }
    // Main gate section.
    gate(12, 9, 2, gateColor);
}

let letters = ["C", "o", "m", "p", "F", "o", "r", "m"];
let xStart = [];
let yStart = [];
let xPos = [];
let yPos = [];
let endX = [100, 250, 400, 550, 100, 250, 400, 550];
let endY = [300, 300, 300, 300, 500, 500, 500, 500];
let easing = 0.08;
let dx = [];
let dy = [];
let gravity = 0.95;
let friction = 0.75;
let velocity = [];
let textS = 100;
let letterColors = []; // Array to store colors for each letter

function setup() {
  createCanvas(800, 700);
  colorMode(HSB, 100, 100, 100);

  textSize(textS);
  textFont('Andale Mono');
  textStyle(BOLD);

  // Generate random colors for each letter
  for (let i = 0; i < letters.length; i++) {
    letterColors[i] = color(random(100), 100, 100);
  }

  for (let i = 0; i < letters.length; i++) {
    xStart[i] = random(0, 700);
    yStart[i] = random(0, 700);
    xPos[i] = xStart[i];
    yPos[i] = yStart[i];
    velocity[i] = random(3,8); // Initialize individual velocities
  }

  // Set the fill color for each letter
  for (let i = 0; i < letters.length; i++) {
    fill(letterColors[i]);
    text(letters[i], xStart[i], yStart[i]);
  }
}

function draw() {
  background(0);

  if (frameCount < 50) {
    // Calculate the distance between current position and final position
    for (let i = 0; i < letters.length; i++) {
      dx[i] = endX[i] - xPos[i];
      dy[i] = endY[i] - yPos[i];
    }

    // Move towards the final position by a fraction of the distance
    for (let i = 0; i < letters.length; i++) {
      xPos[i] += dx[i] * easing;
      yPos[i] += dy[i] * easing;
    }

    for (let i = 0; i < letters.length; i++) {
      text(letters[i], xPos[i], yPos[i]);
      textS += 0.3;
      textSize(textS);
    }

  } else if (frameCount > 50 && frameCount < 270) {
    for (let i = 0; i < letters.length; i++) {
      velocity[i] += gravity;
      yPos[i] += velocity[i];

      // Bounce when hitting the bottom
      if (yPos[i] > height) {
        yPos[i] = height;
        velocity[i] *= -1 * friction;
      }

      fill(letterColors[i]); // Set the fill color for each letter
      text(letters[i], endX[i], yPos[i]);
    }
  }

  console.log(frameCount);
}

let cols, rows;
let scaleSlider, rangeSlider;
let scale = 10;
let terrain = [];

function setup() {
  createCanvas(600, 600);
  cols = width / scale;
  rows = height / scale;

  // Create sliders
  scaleSlider = createSlider(1, 50, 10); 
  scaleSlider.position(20, height + 20);

  rangeSlider = createSlider(0, 255, 100); 
  rangeSlider.position(20, height + 50);

  generateTerrain();
}

function generateTerrain() {
  scale = scaleSlider.value();
  cols = width / scale;
  rows = height / scale;

  terrain = [];

  for (let x = 0; x < cols; x++) {
    terrain[x] = [];
    for (let y = 0; y < rows; y++) {
      terrain[x][y] = random(rangeSlider.value());
    }
  }
}

function draw() {
  background(0);

  // Check if sliders have changed and regenerate terrain if necessary
  if (scale !== scaleSlider.value() || rangeSlider.value() !== terrain[0][0]) {
    generateTerrain();
  }

  for (let x = 0; x < cols - 1; x++) {
    for (let y = 0; y < rows - 1; y++) {
      let x1 = x * scale;
      let x2 = (x + 1) * scale;
      let y1 = y * scale;
      let y2 = (y + 1) * scale;
      let c = terrain[x][y];
      fill(c);
      noStroke();
      rect(x1, y1, scale, scale);

      // Convert grayscale value to ASCII character
      let ascii_char = map(c, 0, 255, 255, 0);
      let character = String.fromCharCode(ascii_char);
      textSize(scale);
      fill(255);
      text(character, x1 + scale / 2, y1 + scale / 2);
    }
  }
}

