function draw() {
  for (let i = 0; i < particles.length; i++) {

    let p = particles[i];
    let r = map(/*p.x*noiseScale,*/ noise(millis() * noiseScale) * noiseLevel, 0, 200, 0, 255);
    stroke(r, 100, 100);
    point(p.x, p.y);
    let n = noise(p.x * noiseScale, p.y * noiseScale);
    let angle = radians(noiseLevel * n);
    p.x += cos(angle);
    p.y += sin(angle);
  }
}

