// Particle array to hold the explosion particles
let particles = [];

function setup() {
  createCanvas(800, 600);
  frameRate(30);
}

function draw() {
  background(0);

  // Each frame, update and display particles
  for (let i = particles.length - 1; i >= 0; i--) {
    particles[i].update();
    particles[i].show();

    // Remove the particle if it's done
    if (particles[i].done()) {
      particles.splice(i, 1);
    }
  }
}

// When the user clicks, explode a firework at the mouse position
function mousePressed() {
  explode(mouseX, mouseY);
}

function explode(x, y) {
  // Create multiple particles from the explosion point
  let particleCount = 100; // Number of particles in the explosion
  for (let i = 0; i < particleCount; i++) {
    particles.push(new Particle(x, y));
  }
}

class Particle {
  constructor(x, y) {
    this.pos = createVector(x, y);
    this.vel = p5.Vector.random2D();
    this.vel.mult(random(1, 6)); // Randomize the speed of the particles
    this.alpha = 255; // Start fully opaque
  }

  update() {
    this.vel.mult(0.95); // Apply some friction to the velocity
    this.pos.add(this.vel);
    this.alpha -= 4; // Fade out the particle
  }

  done() {
    return this.alpha < 0;
  }

  show() {
    noStroke();
    fill(255, this.alpha);
    circle(this.pos.x, this.pos.y, 16);
  }
}

