function createShape(xinit,yinit){
  noStroke();
    vertices = floor(random(3, 8));
  xi = xinit;
  yi = yinit;
  for (let i = 0; i < vertices; i++) {
    deltaX = floor(random(5, 100));
    deltaY = floor(random(5, 100));
    xPos[i] = deltaX + xi;
    yPos[i] = deltaY + yi;
    if (i < vertices / 2) {
      xi += deltaX;
      yi += deltaY;
    } else {
      xi += deltaX;
      yi += deltaY;
    }
  }
  beginShape();
  curveVertex(xinit, yinit);
  for (let j = 0; j < vertices; j++) {
    curveVertex(xPos[j], yPos[j]);
  }
  endShape();
}

