class Coin {
  constructor(x, y, color1, color2, flip, minSize, maxSize, speed) {
    this.x = x;
    this.y = y;

   this.flip = flip === 'h' ? 'h' : 'w'; //chatGPT helped with this line
    this.minSize = minSize;
    this.maxSize = maxSize;

    this.speed = speed;

    this.make(color1, color2);
  }

  make(color1, color2) {
    color(color1)
    color(color2)

    let inter = sin(frameCount * this.speed);
    this.diameter = lerp(this.minSize, this.maxSize, inter)

    noStroke();
    if (inter >= 0) {
      fill(color1);
    } else {
      fill(color2);
    }

    if(this.flip == 'w'){
      ellipse(this.x, this.y, this.maxSize, this.diameter)
    } else if(this.flip == 'h'){
      ellipse(this.x, this.y, this.diameter, this.maxSize)
    }
  }
}

