let img;
let minRes = 7;
let maxRes = 20;
let res = minRes; 
let resIncrement = 0.1;
let colorCycleSpeed = 0.5; 

function preload() {
  img = loadImage('butterfly.png');
}

function setup() {
  createCanvas(img.width, img.height);
  img.loadPixels();
  textAlign(CENTER, CENTER);
  textSize(res);
  textFont('Courier');
  noStroke();
}

function draw() {
  background('#EFEFEC');
  for (let i = 0; i < img.width; i += res) {
    for (let j = 0; j < img.height; j += res) {
      let c = img.get(i, j);
      let character = getCharacter(c);
      let textColor = colorCycle(i, j);
      fill(textColor);
      text(character, i, j);
    }
  }

  // Adjust resolution
  res += resIncrement;
  if (res >= maxRes || res <= minRes) {
    resIncrement *= -1; 
  }
}

function getCharacter(colorValue) {
  let characters = ['B','U','T','T','E','R','F','L','Y',];
  let r = red(colorValue);
  let g = green(colorValue);
  let b = blue(colorValue);

  let index = floor((r + g + b) / 1.5 / 255 * characters.length);
  return characters[index];
}

function colorCycle(x, y) {
  // Calculate color based on sine wave
  let cycle = sin(frameCount * colorCycleSpeed + x * y) * 150 + 150;
  return color(0, cycle, 0); 
}

