let fonts = ["Arial", "Verdana", "Helvetica", "Georgia", "Comic Sans MS", "Impact", "Courier New"];
let sizes = [46, 50, 64, 48, 62, 56, 60];
let interval = 20; 

let currentFrame = 0;
let currentFont, currentSize;

function setup() {
  createCanvas(500, 400);
  background(220);
  textSize(54);
  textAlign(CENTER, CENTER);


  frameRate(30); 
}

function draw() {
  if (frameCount - currentFrame >= interval) {

    currentFont = random(fonts);
    currentSize = random(sizes);

    textSize(currentSize);
    textFont(currentFont);

    let textContent = generateText();


    let textW = textWidthWithFont(textContent, currentFont);
    let textH = textAscent() + textDescent();

    let x = random(width - textW);
    let y = random(height - textH); 

    background(220);
    text(textContent, x, y);

    currentFrame = frameCount;
  }
}

function generateText() {
  let languages = Object.keys(greetings);
  let chosenLanguage = random(languages);
  let words = greetings[chosenLanguage];

  let punctuation = ["!", "?", ".", "..."];

  let text = random(words) + " ";

  // Add some random words to the text
  let numWords = floor(random(1));
  for (let i = 0; i < numWords; i++) {
    text += random(words) + " ";
  }

  // Add random punctuation
  text += random(punctuation);

  return text;
}

function textWidthWithFont(txt, font) {
  textSize(60); // Reset textSize to default for accurate measurement
  return textWidth(txt) * (textSize() / 60) * (textWidth() / textWidth(txt));
}

