let gridSpacing = 20; // The spacing between each plus sign in the grid
let noiseScale = 0.05; // Scale of the noise

function setup() {
  createCanvas(600, 600);
  cols = width / gridSpacing;
  rows = height / gridSpacing;
  loop(); // Start the animation automatically
}

function draw() {
  background(255);
  // Loop through every spot in the grid
  for (let y = 0; y < rows; y++) {
    for (let x = 0; x < cols; x++) {
      // Calculate the 2D noise value for each point
      let noiseVal = noise(x * noiseScale, y * noiseScale, frameCount * noiseScale);

      // Map the noise value to position, size, and rotation
      let posX = x * gridSpacing + noiseVal * gridSpacing - gridSpacing / 2;
      let posY = y * gridSpacing + noiseVal * gridSpacing - gridSpacing / 2;
      let plusSize = map(noiseVal, 0, 1, 5, gridSpacing / 2); // Renamed 'size' to 'plusSize'
      let angle = noiseVal * TWO_PI; // Define angle based on noise

      drawPlus(posX, posY, plusSize, angle); // Use 'plusSize' instead of 'size'
    }
  }
}

function drawPlus(x, y, plusSize, a) {
  push();
  translate(x, y);
  rotate(a);
  stroke(0);
  strokeWeight(2);
  line(-plusSize / 2, 0, plusSize / 2, 0); // Horizontal line
  line(0, -plusSize / 2, 0, plusSize / 2); // Vertical line
  pop();
}

