class Shape {
  constructor(
    x,
    y,
    color1,
    color2,
    color3,
    shape,
    nShape,
    radiusInc,
    r
  ) {
    this.x = x;
    this.y = y;

    this.nShape = nShape;
    this.radiusInc = radiusInc;

    this.r = r;

    this.color1 = color(color1);
    this.color2 = color(color2);
    this.color3 = color(color3);
    this.shapeColors = [this.color1, this.color2, this.color3];

    this.shape = shape;
    this.selectShape();
  }

  selectShape() {
    if (this.shape == "c") {
      for (let i = this.nShape; i > 0; i--) {
        this.diameter = i * this.radiusInc;
        this.shapeColor1 = this.shapeColors[i % 3];

        push();
        translate(this.x, this.y);
        rotate(this.r);

        ellipseMode(CENTER);
        stroke(this.shapeColor1);
        fill(this.shapeColor1);

        circle(0, 0, this.diameter);

        pop();
      }
    } else if (this.shape == "sq") {
      for (let i = this.nShape; i > 0; i--) {
        this.diameter = i * this.radiusInc;
        this.shapeColor1 = this.shapeColors[i % 3];

        push();
        translate(this.x, this.y);
        rotate(this.r);

        rectMode(CENTER);
        stroke(this.shapeColor1);
        fill(this.shapeColor1);

        rect(0, 0, this.diameter);

        pop();
      }
    } else {
      console.log("selectShape() no worky");
    }
  }
}

