let cols, rows;
let scl = 20;
let w = 1400;
let h = 800;

let angle = 0;

let terrain = [];
let whiteCubes = []; // Store white cubes

let brownBlockX, brownBlockY;

function setup() {
  createCanvas(800, 800, WEBGL);
  cols = w / scl;
  rows = h / scl;

  for (let x = 0; x < cols; x++) {
    terrain[x] = [];
    for (let y = 0; y < rows; y++) {
      terrain[x][y] = 0; // Initialize terrain values
    }
  }

  // Clouds
  for (let i = 0; i < 80; i++) {
    let x = random(-width / 2, width / 2); 
    let z = random(200, 0); 
    let w = random(10, 40); 
    let h = random(10, 10); 
    let transparency = random(100, 255); 
    let rotationY = (-PI / 3, PI / 3); // Random rotation around Y axis
    whiteCubes.push({ x, z, w, h, transparency, rotationX, rotationY });
  }

  brownBlockX = floor(random(cols));
  brownBlockY = floor(random(rows));
}

function draw() {
  angle -= 0.1;

  let yoff = angle;
  for (let y = 0; y < rows; y++) {
    let xoff = 0;
    for (let x = 0; x < cols; x++) {
      terrain[x][y] = map(noise(xoff, yoff), 0, 1, -100, 100);
      xoff += 0.05; 
    }
    yoff += 0.1; 
  }

  background(135, 206, 250);

  // Draw clouds
  for (let i = 0; i < whiteCubes.length; i++) {
    let cube = whiteCubes[i];
    fill(255, cube.transparency);
    noStroke();
    push();
    translate(cube.x, -height / 2 + 120, cube.z); 
    rotateX(cube.rotationX); 
    rotateY(cube.rotationY);
    box(cube.w, cube.h, cube.w); 
    pop();
  }

  // Draw terrain
  rotateX(PI / 3);
  translate(-w / 2, -h / 2);
  for (let y = 0; y < rows - 1; y++) {
    for (let x = 0; x < cols; x++) {
      let c;
      if (x === brownBlockX && y === brownBlockY) {
        c = color(139, 69, 19); // treasure block
      } else {
        c = color(110, 161, 90); 
      }
      fill(c);
      stroke(0);
      strokeWeight(1);
      let heightValue = terrain[x][y];
      push();
      translate(x * scl, y * scl, heightValue);
      box(scl, scl, scl);
      pop();
    }
  }
}

