// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js
// referenced the Circle Grid code from in Comp Form Noise Chapter
// https://compform.net/noise/ by Justin Bakse
// help from GPT to lower the opacity for smaller rects

let yellowCircleX;
let yellowCircleY;

function setup() {
  createCanvas(600, 600);
  rectMode(CENTER);
  ellipseMode(CENTER);
  noStroke();

  // Generate random coordinates for the yellow circle
  yellowCircleX = random(width/2-10);
  yellowCircleY = random(height);
}

function draw() {
  background('#000000');
  let n;
  let totalRectangles = 0;
  let totalSize = 0;

  for (let y = 20; y < height; y += 40) {
    for (let x = 20; x < width; x += 40) {
      n = noise(x * 0.01, y * 0.04);
      let shapeSizeX = n * 80; // Adjusted for more drastic size
      n = noise(x * 0.5, y * 0.5);
      let shapeSizeY = n * 60;

      totalRectangles++;
      totalSize += min(shapeSizeX, shapeSizeY);

      push();

      // opacity based on the size of the rectangle
      let opacity = map(min(shapeSizeX, shapeSizeY), 0, 100, 100, 255);

      // lower opacity for smaller rects smaller than the average size
      if (min(shapeSizeX, shapeSizeY) < totalSize / totalRectangles) {
        opacity *= 0.1; 
      }

      // adjusted opacity
      let fillColor = color('#4A45AF');
      fillColor.setAlpha(opacity);

      fill(fillColor);
      rect(x, y, shapeSizeX, shapeSizeY, shapeSizeX * 0.2);

      pop();
    }
  }

  //treasure
  fill('#FFFF00'); 
  ellipse(yellowCircleX, yellowCircleY, 15, 15); 
}

