function lines_1() {
  for (let x = 50; x <= 550; x += 60) {
    let x1 = x;
    let y1 = 0;
    let x2 = x;
    let y2 = 600;

    n = noise((x + offsetX) * 0.2);
    let currentOffsetX = n * 20 - 40;

    line(x1 + currentOffsetX, y1, x2 - currentOffsetX, y2);
  }
}

function lines_2() {
  for (let y = 100; y <= 550; y += 60) {
    let x1 = 0;
    let y1 = y;
    let x2 = 600;
    let y2 = y;

    n = noise((y + offsetY) * 0.2);
    let currentOffsetY = n * 20 - 40;

    line(x1, y1 - currentOffsetY, x2, y2 + currentOffsetY);
  }
}

