function fry() {
  background(0);

  oompaLoompa.loadPixels();

  for (let y = 0; y < oompaLoompa.height; y++) {
    for (let x = 0; x < oompaLoompa.width; x++) {
      const c = getQuick(oompaLoompa, x, y);
      //console.log('c',c);
      const r = c[0];
      const g = c[1];
      const b = c[2];
      let change = 25;

      if (r > g && r > b) {
        c[0] += change;
      } else if (g > r && g > b) {
        c[1] += change;
      } else if (b > g && b > r) {
        c[2] += change;
      }
      if (r < g && r < b) {
        c[0] -= change;
      } else if (g < r && g < b) {
        c[1] -= change;
      } else if (b < g && b < r) {
        c[2] -= change;
      }
      //console.log('new c',c);
      setQuick(oompaLoompa, x, y, c);
    }
  }

  oompaLoompa.updatePixels();

  noSmooth();
  image(oompaLoompa, 0, 0, width, height);
  wonkaLevel += 1;
  text(`Wonka Level: ${wonkaLevel}`, width / 2, 350);
}

function getQuick(img, x, y) {
  const i = (y * img.width + x) * 4;
  return [
    img.pixels[i],
    img.pixels[i + 1],
    img.pixels[i + 2],
    img.pixels[i + 3],
  ];
}

function setQuick(img, x, y, c) {
  const i = (y * img.width + x) * 4;

  img.pixels[i + 0] = c[0];
  img.pixels[i + 1] = c[1];
  img.pixels[i + 2] = c[2];
  img.pixels[i + 3] = c[3];
}

var img;
let pixelation_level = 15;
let switchInterval = 60; // Number of frames before switching corners
let currentCorner = 0; // 0 for left bottom corner, 1 for right upper corner

function preload() {
  img = loadImage('https://assets.speakcdn.com/assets/2941/dart_frog_(1).jpg');
}

function setup() {
  createCanvas(800, 800);
  noStroke();
}

function draw() {
  pixelDensity(1); // streamlining pixelDensity
  image(img, 0, 0, width, height);
  loadPixels(); // create the pixels[] array from our image
  noStroke();

  if (frameCount % switchInterval === 0) {
    currentCorner = (currentCorner + 1) % 2; 
  }

  if (currentCorner === 0) {
    pixelateCorner(0, height / 2); 
  } else {
    pixelateCorner(width / 2, 0); 
  }
}

function pixelateCorner(xStart, yStart) {
  for (let x = xStart; x < xStart + width / 2; x += pixelation_level) {
    for (let y = yStart; y < yStart + height / 2; y += pixelation_level) {
      let i = (x + y * width) * 4;

      let r = pixels[i + 0];
      let g = pixels[i + 1];
      let b = pixels[i + 2];
      let a = pixels[i + 3];

      fill(r, g, b, a);
      square(x, y, pixelation_level);
    }
  }
}

let spacingSlider, loopsSlider; 

function setup() {
  createCanvas(600, 600); // Set the size of the canvas
  background(30, 45, 90); // Set a blue background, similar to the image
  noFill(); // Ensure the spiral has no fill
  stroke(255); // Set the spiral color to white
  strokeWeight(2); // Set the width of the spiral line
  drawSpiral(width / 2, height / 2, 10, 10, 20); // Draw the spiral
  // Create a slider for spacing between loops (min value, max value, initial value)
  let spacingLabel = createDiv('Spacing Between Loops');
  spacingLabel.position(10, height + 5).style('color', 'black').style('font-family', 'Roboto, sans-serif');
  spacingSlider = createSlider(5, 20, 10);
  spacingSlider.position(10, height + 20); // Position it below the canvas

  // Create a slider for total loops (min value, max value, initial value)
  let loopsLabel = createDiv('Total Loops');
  loopsLabel.position(10, height + 35).style('color', 'black').style('font-family', 'Roboto, sans-serif');
  loopsSlider = createSlider(5, 50, 15);
  loopsSlider.position(10, height + 50); // Position it below the first slider
}

function draw() {
  // Only draw when a slider is adjusted to prevent continuous redraw
  if (mouseIsPressed || frameCount === 1) {
    redrawSpiral();
  }
}

function redrawSpiral() {
  background(30, 45, 90); // Reset background
  noFill();
  stroke(255);
  strokeWeight(2);

  // Use the slider values for spacing and total loops
  let spacingBetweenLoops = spacingSlider.value();
  let totalLoops = loopsSlider.value();

  drawSpiral(width / 2, height / 2, 10, spacingBetweenLoops, totalLoops);
}

function drawSpiral(centerX, centerY, initialRadius, spacingBetweenLoops, totalLoops) {
  let angle = 0;
  let radius = initialRadius;
  beginShape();
  for (let i = 0; i < totalLoops * 360; i++) {
    angle = radians(i); // Convert angle to radians
    radius += spacingBetweenLoops / (100 * PI); // Increment radius
    let x = centerX + radius * cos(angle);
    let y = centerY + radius * -sin(angle);
    vertex(x, y); // Draw spiral segment
  }
  endShape();
}

