function calcNights(){
    // Get the current date
    var currentDate = new Date();

    // Set the target date to 17th May of the current year
    var targetDate = new Date(currentDate.getFullYear(), 4, 17); // Month is 0-based, so May is represented by 4

    // Calculate the difference in milliseconds between the current date and the target date
    var timeDifference = targetDate.getTime() - currentDate.getTime();

    // Calculate the number of milliseconds in a day
    var millisecondsInADay = 1000 * 60 * 60 * 24;

    // Calculate the number of nights left
    var nightsLeft = Math.ceil(timeDifference / millisecondsInADay);

    return nightsLeft;
}

