let saturationSlider;
let cols, rows;
let triangleWidth, triangleHeight;

function setup() {
  createCanvas(500, 500);
  colorMode(HSB, 360, 100, 100); // Use HSB color mode
  noLoop();
  noStroke();

  cols = 32;
  rows = 20;
  triangleWidth = width / cols;
  triangleHeight = (height / rows) * 2; // Each row contains two layers of triangles

  // Create a slider for saturation
	createDiv('Saturation').position(10, height + 35).style('color', 'white').style('font-family', 'Roboto, sans-serif');
  saturationSlider = createSlider(0, 100, 50);
  saturationSlider.position(10, height + 10);
  saturationSlider.style('width', '80px');
  saturationSlider.input(draw); // Redraw when slider value changes
}

function draw() {
  background(0, 0, 0); // White background in HSB mode
  let saturation = saturationSlider.value();

  for (let y = 0; y < rows; y++) {
    for (let x = 0; x < cols; x++) {
      // Each triangle gets a random hue and fixed saturation and brightness
      let hue = random(360);
      fill(hue, saturation, 100);

      // Draw two triangles to form a diamond shape
      let x1 = x * triangleWidth;
      let y1 = y * triangleHeight / 2;
      triangle(x1, y1, x1 + triangleWidth / 2, y1 + triangleHeight / 2, x1 - triangleWidth / 2, y1 + triangleHeight / 2); // Upper triangle
      triangle(x1, y1, x1 + triangleWidth / 2, y1 - triangleHeight / 2, x1 - triangleWidth / 2, y1 - triangleHeight / 2); // Lower triangle
    }
  }
}

