//requires "https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.9.0/p5.js"

class Building {
  constructor(x, y, x2, y2, sWeight, sColor, fColor) {
    this.x = x;
    this.y = y;
    this.w = x2-x;
    this.h = y2-y;
    this.fColor = fColor;
    this.sColor = sColor;
    this.sWeight = sWeight;
  }

  make() {

    rectMode()
    strokeWeight(this.sWeight);
    stroke(this.sColor);

    //middle rect
    fill(this.fColor);
    rect(this.x, this.y, this.w, this.h);

    noFill()
    //outside rect
    rect(
      this.x - this.x/4, 
      this.y - this.y/4, 
      this.w + this.w/2, 
      this.h + this.h/2);

    //inside rect
     rect(
      this.x + this.x/4, 
      this.y + this.y/4, 
      this.w - this.w/2, 
      this.h - this.h/2);
  }
}


