function setup() {
  createCanvas(700, 700);
  colorMode(HSB, 255, 100, 100);
}

function draw() {
  background(255);

  lightness = params.lightness;
  saturation = params.saturation;

  // Iterate through every fifth pixel for load time
  for (let x = 0; x < width; x += spacing) {
    for (let y = 0; y < height; y += spacing) {
      let closestDistance = Infinity;

      // Find the closest point from the array
      for (let i = 0; i < xPoints.length; i++) {
        let d = dist(x, y, xPoints[i], yPoints[i]);
        closestDistance = min(closestDistance, d);
      }

      // Set color based on closest distance
      let colorValue;
      if (closestDistance <= 50) {
        colorValue = color(255, lightness, saturation); // Yellow
      } else if (closestDistance > 50 && closestDistance <= 100) {
        colorValue = color(25, lightness, saturation); // Green
      } else if (closestDistance > 100 && closestDistance <= 150) {
        colorValue = color(150, lightness, saturation); // Red
      } else if (closestDistance > 150 && closestDistance <= 200) {
        colorValue = color(55, lightness, saturation); // Blue
      } else if (closestDistance > 200 && closestDistance <= 250){
        colorValue = color(2, lightness, saturation); // Black for the rest
      } else if (closestDistance > 250 && closestDistance <= 300){
        colorValue = color(199, lightness, saturation); // Black for the rest
      } else if (closestDistance > 300 && closestDistance <= 350){
        colorValue = color(120, lightness, saturation); // Black for the rest
      } else if (closestDistance > 350 && closestDistance <= 400){
        colorValue = color(170, lightness, saturation); // Black for the rest
      } else if (closestDistance > 400 && closestDistance <= 450){
        colorValue = color(13, lightness, saturation); // Black for the rest
      } else if (closestDistance > 450 && closestDistance <= 500){
        colorValue = color(99, lightness, saturation); // Black for the rest
      } else if (closestDistance > 500 && closestDistance <= 550){
        colorValue = color(220, lightness, saturation); // Black for the rest
      } else if (closestDistance > 550 && closestDistance <= 600){
        colorValue = color(140, lightness, saturation); // Black for the rest
      } else if (closestDistance > 600 && closestDistance <= 650){
        colorValue = color(110, lightness, saturation); // Black for the rest
      } else if (closestDistance > 650 && closestDistance <= 700){
        colorValue = color(10, lightness, saturation); // Black for the rest
      }

      // Apply color to current and neighboring pixels so no white space
      for (let i = 0; i < spacing; i++) {
        for (let j = 0; j < spacing; j++) {
          if (x + i < width && y + j < height) {
            set(x + i, y + j, colorValue);
          }
        }
      }
    }
  }
  updatePixels(); // Update canvas with modified pixel values
}


