let constellations = [];
let currentConstellation;
let changeInterval = 200; // change the zodiac every 0.2 sec
let lastChangeTime = 0;
let fadeAmount = 255; // start with fully visible zodiac
let fadeDecrement = 5; // decrease the alpha value for a lighter zodiac
let zodiacColor; // variable to control zodiac color

// Define constellation patterns
const constellationPatterns = [
  // Aries
  { stars: [[-20, 0], [0, -30], [20, 0]], connections: [[0, 1], [1, 2]] },
  // Taurus
  { stars: [[-20, -20], [20, -20], [0, 0], [40, 0]], connections: [[0, 1], [1, 2], [2, 3]] },
  // Gemini
  { stars: [[-20, 0], [-20, -20], [20, -20], [20, 0]], connections: [[0, 1], [1, 2], [2, 3]] },
  // Cancer
  { stars: [[0, -20], [20, -40], [40, -20]], connections: [[0, 1], [1, 2]] },
  // Leo
  { stars: [[-20, 0], [-40, -20], [-20, -40], [0, -20], [20, -40]], connections: [[0, 1], [1, 2], [3, 4]] },
  // Virgo
  { stars: [[-20, -20], [0, 0], [20, -20], [40, 0]], connections: [[0, 1], [1, 2], [2, 3]] },
  // Libra
  { stars: [[-20, 0], [0, -20], [20, 0]], connections: [[0, 1], [1, 2]] },
  // Scorpio
  { stars: [[-40, -20], [-20, 0], [0, -20], [20, 0]], connections: [[0, 1], [1, 2], [2, 3]] },
  // Sagittarius
  { stars: [[-30, 0], [-10, -20], [10, 0], [30, -20]], connections: [[0, 1], [1, 2], [2, 3]] },
  // Capricorn
  { stars: [[0, -20], [20, 0], [40, -20]], connections: [[0, 1], [1, 2]] },
  // Aquarius
  { stars: [[-40, 0], [-20, -20], [0, 0], [20, -20]], connections: [[0, 1], [1, 2], [2, 3]] },
  // Pisces
  { stars: [[-20, -20], [0, 0], [20, -20], [40, 0]], connections: [[0, 1], [1, 2], [2, 3]] }
];

function setup() {
  createCanvas(600, 600);
  frameRate(5); // Increase frame rate to change constellations more frequently

  // Initialize constellations with random positions
  initializeConstellations();

  // Randomize the starting constellation
  currentConstellation = floor(random(constellations.length));

  // Set initial zodiac color
  zodiacColor = color(255);
}

function initializeConstellations() {
  constellations = []; // Clear the previous constellations
  for (let pattern of constellationPatterns) {
    let offsetX = random(width);
    let offsetY = random(height);
    let stars = pattern.stars.map(star => [star[0] + offsetX, star[1] + offsetY]);
    let connections = pattern.connections;
    constellations.push({ stars, connections });
  }
}

function draw() {
  let currentTime = millis();
  // Check if it's time to change the constellation
  if (currentTime - lastChangeTime > changeInterval) {
    lastChangeTime = currentTime;
    currentConstellation = floor(random(constellations.length));
    initializeConstellations(); // Reinitialize constellations for new random positions
    fadeAmount = 255; // reset the fade amount
    zodiacColor = color(random(150, 255), random(150, 255), random(150, 255)); // set a new random zodiac color
  }

  // Draw galaxy-like background
  drawGalaxyBackground();

  // Draw the current constellation with fading effect
  const constellation = constellations[currentConstellation];
  drawConstellation(constellation);
}

function drawGalaxyBackground() {
  // Gradient effect for galaxy background
  for (let i = 0; i <= width; i++) {
    let inter = map(i, 0, width, 0, 1);
    let c = lerpColor(color(0, 10, 25), color(25, 0, 51), inter);
    stroke(c);
    line(i, 0, i, height);
  }

  // Stars in the background for galaxy effect
  for (let i = 0; i < 100; i++) {
    let x = random(width);
    let y = random(height);
    let size = random(1, 3);
    fill(255);
    noStroke();
    ellipse(x, y, size, size);
  }
}

function drawConstellation(constellation) {
  stroke(zodiacColor);
  strokeWeight(2);
  for (let i = 0; i < constellation.connections.length; i++) {
    let startStar = constellation.stars[constellation.connections[i][0]];
    let endStar = constellation.stars[constellation.connections[i][1]];
    line(startStar[0], startStar[1], endStar[0], endStar[1]);
  }

  fill(zodiacColor);
  for (let i = 0; i < constellation.stars.length; i++) {
    let star = constellation.stars[i];
    ellipse(star[0], star[1], 5, 5);
  }
}

let diceFaceSize = 100; // Size of each dice face
let diceMargin = 20; // Margin between dice faces
let totalFaces = 6; // Total number of faces on each dice
let currentFace1 = 1; // Variable to store the current face shown on dice 1
let currentFace2 = 1; // Variable to store the current face shown on dice 2
let chosenOption = 0; // Variable to store the chosen option by the player
let rollingDice = false; // Variable to indicate whether the dice are rolling
let options = []; // Array to store random options

function setup() {
  createCanvas(400, 400);
  generateRandomOptions();
}

function draw() {
  background(220);

  // Display "Guess your number" text
  textSize(20);
  fill(0); // Set the fill color to black for the text
  textAlign(CENTER, CENTER);
  text("Guess your number", width / 2, 30); // Position the text at the top center

  // Display random options
  textSize(24);
  options.forEach((option, index) => {
    let xPosition = (index + 1) * width / (options.length + 1);
    text(option, xPosition, height / 2);
  });

  // Display dice faces
  displayDiceFace(currentFace1, width / 4, height - 100);
  displayDiceFace(currentFace2, 3 * width / 4, height - 100);

  // Check if an option has been chosen and the dice are not rolling
if (!rollingDice && chosenOption !== 0) { // Make sure the dice roll has finished and an option was chosen
    let sum = currentFace1 + currentFace2;
    textSize(32);
    textAlign(CENTER, CENTER);
    text("Sum: " + sum, width / 2, height / 2 + 40); // Confirm this is visible

    // Display win/lose message
    if (options.includes(sum)) {
      fill(0, 255, 0); // Green for win
      text("Win!", width / 2, height / 2 + 80); // Confirm this is visible
    } else {
      fill(255, 0, 0); // Red for lose
      text("Lose", width / 2, height / 2 + 80); // Confirm this is visible
    }

  }
}
function mouseClicked() {
  if (!rollingDice) {
    for (let index = 0; index < options.length; index++) {
      let option = options[index];
      let optionX = (index + 1) * width / (options.length + 1);
      if (mouseX > optionX - 50 && mouseX < optionX + 50 && mouseY > height / 2 - 30 && mouseY < height / 2 + 30) {
        chosenOption = option;
        rollDice();
        return; // Use 'return' instead of 'break' to exit the function
      }
    }
  }
}

function rollDice() {
  // Choose a random sum from the available options
  let sumIndex = floor(random(0, options.length));
  let targetSum = options[sumIndex];

  rollingDice = true;
  setTimeout(() => {
    // Randomly choose new dice faces until the target sum is reached
    do {
      currentFace1 = floor(random(1, totalFaces + 1));
      currentFace2 = floor(random(1, totalFaces + 1));
    } while (currentFace1 + currentFace2 !== targetSum);

    rollingDice = false;
    // Reset chosenOption to 0 to wait for the next selection
    chosenOption = 0; 
    // Generate new options for the next selection
    generateRandomOptions();
  }, 1000);
}

function displayDiceFace(face, x, y) {
  // Draw the outline of the dice
  rectMode(CENTER);
  fill(255);
  stroke(0);
  rect(x, y, diceFaceSize, diceFaceSize, 10);

  // Draw the dots based on the face value
  fill(0);
  noStroke();
  let dotSize = 10;
  let spacing = 20;
  let startX = x - spacing;
  let startY = y - spacing;
  let endX = x + spacing;
  let endY = y + spacing;

  // Display dots based on the face value
if (face === 1 || face === 3 || face === 5) { // Corrected 'or' to '||'
  ellipse(x, y, dotSize, dotSize); // Center dot
}
if (face >= 2) {
  ellipse(startX, startY, dotSize, dotSize); // Top left dot
  ellipse(endX, endY, dotSize, dotSize); // Bottom right dot
}
if (face >= 4) {
  ellipse(endX, startY, dotSize, dotSize); // Top right dot
  ellipse(startX, endY, dotSize, dotSize); // Bottom left dot
}
if (face === 6) {
  ellipse(startX, y, dotSize, dotSize); // Middle left dot
  ellipse(endX, y, dotSize, dotSize); // Middle right dot
}
	}


function generateRandomOptions() {
  options = []; // Clear previous options
  while (options.length < 3) {
    let randomOption = floor(random(1, totalFaces * 2 + 1)); // Generate random options within the possible sum range (2-12)
    if (!options.includes(randomOption)) {
      options.push(randomOption);
    }
  }
}

