function setup() {
  createCanvas(500, 300);

  createP("Amplitude");
  amplitude_slider = createSlider(0, 100, 50);

  createP("Time Speed");
  timeSpeed_slider = createSlider(0, 25, 10);

  createP("Frequency");
  frequency_slider = createSlider(0, 30, 5);
}

function draw() {
  background(50);
  ellipseMode(CENTER);

  let amplitude = amplitude_slider.value()/5;
  let frequency = frequency_slider.value();
  let timeSpeed = timeSpeed_slider.value() / 100;
  let nt = timeSpeed * frameCount;

  noiseDetail(1, timeSpeed/100);

  fill(255);
  noStroke();

  // study this loop. do you understand how the line of ellipses is created?
  for (i = 0; i < 1; i += 0.02) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    // hint: drive offsetX and offsetY with noise() instead of random()
    let offsetX = (noise(nt+i*frequency) - 0.5) * amplitude * 15;
    let offsetY = (noise(nt+i*frequency) - 0.5) * amplitude * 15;

    ellipse(x + offsetX, y + offsetY, 10, 10);
  }
}

