let maxPoints = 1000; // max points in triangle
let delay = 1; // delay between each iteration
let points = []; // array to store points

function setup() {
  createCanvas(400, 400);
  noStroke();
  fill('white');

  // initializing points for anim
  points.push([100, 100]);
 points.push([300, 300]);
 points.push([200, 200]);
}

function draw() {
  background('black');
  // draw points
  for (let i = 0; i < points.length; i++) {
    point(points[i][0], points[i][1]);
  }

  // stop animation at maxpoints
  if (points.length >= maxPoints) {
    noLoop();
  }

  // generate new points with recursion
  generatePoints();
}

function generatePoints() {
  const p1 = points[floor(random(3))];
  const p2 = points[points.length - 1];
  const p3 = midpoint(p1, p2);
  points.push(p3);
  //draw line b/w 2points
 if (points.length < maxPoints) {
   strokeWeight(1);
   stroke('white');
   line(p1[0], p1[1], p2[0], p2[1]);
 }
  if (points.length < maxPoints) {
    setTimeout (generatePoints, delay);
}
}
  function midpoint(p1, p2) {
  return [(p1[0] + p2[0]) / 2, (p1[1] + p2[1]) / 2];
}

