let reverseSlider, smallerSlider;
let s;

function setup() {
  createCanvas(400, 400);

  //Create slider to reverse black and white color
  reverseSlider = createSlider(0, 1, 0, 1);
  reverseSlider.position(10, height + 5);
  //Create slider to add squares
  smallerSlider = createSlider(0, 10, 0);
  smallerSlider.position(10, height + 30);
}

function draw() {
  background(220);
  noStroke();

  //Reverse is false to begin with, and becomes true if the slider is at 1 (right value) to allow colors to be reversed 
  let reverse = reverseSlider.value() == 1;

  // Draw squares
  fill(reverse ? 255 : 0); // Top left - condition each time to make it white or black depending on reverse function 
  rect(0, 0, 200, 200);
  fill(reverse ? 0 : 255); // Top right
  rect(200, 0, 200, 200);
  fill(reverse ? 255 : 0); // Bottom right
  rect(200, 200, 200, 200);
  fill(reverse ? 0 : 255); // Bottom left
  rect(0, 200, 200, 200);

  // Smaller squares
  let smallerCount = smallerSlider.value();
  for (let i = 0; i < smallerCount; i++) {
    for (let j = 0; j < smallerCount; j++) {
      let s = 200 / smallerCount;
      fill((i + j) % 2 == 0 ? (reverse ? 0 : 255) : (reverse ? 255 : 0));
      rect(i * s, j * s, s, s);
      rect(200 + i * s, 200 + j * s, s, s);

      fill((i + j) % 2 == 0 ? (reverse ? 255 : 0) : (reverse ? 0 : 255));
      rect(200 + i * s, j * s, s, s);
      rect(i * s, 200 + j * s, s, s);
    }
  }
}

