function drawBranch(length) {
  if (length < 2) {
    return;
  }

  // draw this branch
  strokeWeight(length / 10);
  myTurtle.moveForward(length * 0.2);
  myTurtle.turnLeft(10);
  myTurtle.moveForward(length * 0.6);

  // left child
  myTurtle.pushState();
  myTurtle.turnLeft(random(20, 40));
  drawBranch(length * 0.75);
  //drawBranch(length * random(.5, .9));

  myTurtle.popState();

  // right child
  myTurtle.pushState();
  myTurtle.turnRight(random(10, 30));
  drawBranch(length * 0.75);
  //drawBranch(length * random(.5, .9));
  myTurtle.popState();
}

