function paint(sw, n, r1, r2, g1, g2, b1, b2, a1, a2, x1, x2, y1, y2) {
  strokeWeight(sw);
  for (let i = 0; i < n; i++) {
    stroke(random(r1, r2), random(g1, g2), random(b1, b2), random(a1, a2));
    myTurtle.moveForward(random(3));
    myTurtle.penUp();
    myTurtle.moveTo(random(x1, x2), random(y1, y2));
    myTurtle.penDown();
  }
}

//asked chatGPT to make me a function that could paint in a circle instead of a square
function paintCircle(sw, r1, r2, g1, g2, b1, b2, a1, a2, centerX, centerY, radius) {
  strokeWeight(sw);
  for (let x = centerX - radius; x <= centerX + radius; x++) {
    for (let y = centerY - radius; y <= centerY + radius; y++) {
      if ((x - centerX) * (x - centerX) + (y - centerY) * (y - centerY) <= radius * radius) {
        stroke(random(r1, r2), random(g1, g2), random(b1, b2), random(a1, a2));
        myTurtle.moveForward(random(3))
                             }
    }
  }
}


