let amplitude_slider;
let frequency_slider;
let timeSpeed_slider;

let startX = 50;
let startY = 250;
let endX = 450;
let endY = 70;

function setup() {
  createCanvas(500, 300);
  createP('Frequency');
  frequency_slider = createSlider(0, 100, 50);

  createP('Amplitude');
  amplitude_slider = createSlider(0, 100, 50);

  createP('Time Speed');
  timeSpeed_slider = createSlider(0, 100, 50);
}

function draw() {
  background(50);
  ellipseMode(CENTER);

  let amplitude = amplitude_slider.value() / 35;
  let frequency = frequency_slider.value() / 1.2;
  let timeSpeed = (timeSpeed_slider.value() * frameCount) / 700;

  noiseDetail(1, timeSpeed);

  fill(255);
  noStroke();

  // study this loop. do you understand how the line of ellipses is created?
  for (i = 0; i < 1; i += 0.02) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    // hint: drive offsetX and offsetY with noise() instead of random()
    let offsetX = (0.5 - noise(timeSpeed + i * frequency)) * amplitude * 30;
    let offsetY = (0.5 - noise(timeSpeed + i * frequency)) * amplitude * 30;

    ellipse(x + offsetX, y + offsetY, 10, 10);
  }
}

//chat gpt to create Anni Albers, "Triangulated Intaglio IV," 1976, s
let rows = 17;
let cols = 15;
let gridSize = 20; // Adjust the grid size
let padding = 170; // Adjust the padding value as needed

function setup() {
  createCanvas(cols * gridSize + 2 * padding, rows * gridSize + 2 * padding);
  background(253, 247, 240);
  drawPattern(padding);
}

function drawPattern(padding) {
  for (let x = 0; x < cols; x++) {
    for (let y = 0; y < rows; y++) {
      let orientation = '';

      if (random(1) < 0.33) {
        orientation = 'left';
      } else if (random(1) < 0.66) {
        orientation = 'right';
      } else {
        orientation = 'opposite';
      }

      drawTriangle(x, y, orientation, padding);
    }
  }
}

function drawTriangle(x, y, orientation, padding) {
  if (orientation === 'left') {
    fill(172, 92, 62);
    noStroke();
    triangle(padding + x * gridSize, padding + y * gridSize,
             padding + (x + 1) * gridSize, padding + y * gridSize,
             padding + x * gridSize, padding + (y + 1) * gridSize);
  } else if (orientation === 'right') {
    fill(172, 92, 62);
    noStroke();
    triangle(padding + (x + 1) * gridSize, padding + y * gridSize,
             padding + (x + 1) * gridSize, padding + (y + 1) * gridSize,
             padding + x * gridSize, padding + (y + 1) * gridSize);
  } else if (orientation === 'opposite') {
    fill(172, 92, 62);
    noStroke();
    triangle(padding + x * gridSize, padding + y * gridSize,
             padding + (x + 1) * gridSize, padding + y * gridSize,
             padding + (x + 1) * gridSize, padding + (y + 1) * gridSize);
  }
}

