function drawEye(x, y, radius) {
  myTurtle.penUp();
  strokeWeight(2);

  myTurtle.moveTo(x, y - radius);
  myTurtle.penDown();
  //chat gpt for help with circle
  let circumference = 1.5 * PI * radius;
  let numSteps = circumference;
  let stepLength = circumference / numSteps;
  let stepAngle = 360 / numSteps;

  for (let i = 0; i < numSteps; i++) {
    myTurtle.moveForward(stepLength);
    myTurtle.turnRight(stepAngle);
  }
}

