let capture;
let radius =45;
  let pixelSize = 3; 

function setup() {
  createCanvas(500, 400);
  capture = createCapture(VIDEO);
  capture.size(width, height);
  capture.hide();
  noStroke();
  ellipseMode(CENTER);
}

function draw() {
  background(0);
  capture.loadPixels();
  processThis();
}

function processThis() {

  for (let y = 0; y < height; y += pixelSize) {
    for (let x = 0; x < width; x += pixelSize) {
      let distance = dist(x, y, mouseX, mouseY); 
      if (distance > radius) {

        const index =
          (floor((y * capture.height) / height) * capture.width +
            floor((x * capture.width) / width)) *
          4;
        const r = capture.pixels[index] - 100;
        const g = capture.pixels[index + 1] - 100;
        const b = capture.pixels[index + 2];

        fill(r, g, b);
        ellipse(x, y, pixelSize, pixelSize);
      } else{
        let px = int(map(x, 0, width, 0, capture.width));
        let py = int(map(y, 0, height, 0, capture.height));
        let col = capture.get(px, py);
        fill(col);
        rect(x, y, pixelSize, pixelSize);
      }
    }
  }
}

