let myTurtle;
let strokeColor;

function setup() {
  createCanvas(windowWidth, windowHeight);
  angleMode(DEGREES);
  myTurtle = new Turtle();
  strokeColor = color(0);
}

function draw() {
  background(0);
  frameRate(10)

  noFill();
  strokeWeight(0.5);

  // move to starting position (without drawing)
  myTurtle.penUp();
  myTurtle.moveTo(width / 2, height / 2);

  let sideLength = 20; // initial side length
  let angleIncrement = noise(1) * 50;
  let steps = 150;
  for (let i = 0; i < steps; i++) {
    // Set the stroke color
    stroke(strokeColor);

    // draw the triangle outline
    beginShape();
    for (let j = 0; j < 20; j++) {
      myTurtle.moveForward(sideLength);
      myTurtle.turnRight(10);
      vertex(myTurtle.x, myTurtle.y);
    }
    endShape(CLOSE);
    myTurtle.moveTo(width / 3, height/2);

    sideLength += 1;
    myTurtle.turnRight(angleIncrement);

    strokeColor.levels[0] = (strokeColor.levels[0] + 100*(i%3)) % 256;
    strokeColor.levels[1] = (strokeColor.levels[1] + 50*(i%3)) % 256;
    strokeColor.levels[2] = (strokeColor.levels[2] + 10*(i%3)) % 256;
  }

  noLoop();
}

