let frequency_slider, amplitude_slider, timeSpeed_slider;

let points = [];

function setup() {
  createCanvas(500, 300);

  createP("Frequency");
  frequency_slider = createSlider(0, width, width * 0.15);

  createP("Amplitude");
  amplitude_slider = createSlider(0, 100, 50);

  createP("Time Speed");
  timeSpeed_slider = createSlider(0, 100, 50);
}

function draw() {
  background(50);
  ellipseMode(CENTER);

  let frequency = frequency_slider.value() / 20;
  let amplitude = amplitude_slider.value() / 25;
  let timeSpeed = timeSpeed_slider.value() / 80;
  let noiseTS = abs(timeSpeed) * frameCount * .2;

  noiseDetail(1, timeSpeed / 100);

  // inside the canvas
  if (mouseX >= 0 && mouseX <= width && mouseY >= 0 && mouseY <= height) {
    let mouseXPos = mouseX;
    let mouseYPos = mouseY;

    points.push({ x: mouseXPos, y: mouseYPos });

    if (points.length > 200) {
      points.shift();
    }
  }

  for (let i = 0; i < points.length; i++) {
    let x = points[i].x;
    let y = points[i].y;

    let offsetX = (noise(noiseTS + i * frequency * 1.2) - 0.5) * amplitude * 15;
    let offsetY = (noise(noiseTS + i * frequency) - 0.5) * amplitude * 15;

    // Decrease opacity along the path
    let alpha = map(i, 0, points.length - 1, 255, 0);

    fill(255, alpha);
    noStroke();
    ellipse(x + offsetX, y + offsetY, 10, 10);
  }
}

