function setup() {
  createCanvas(400, 400);
  frameRate(30); // Set frame rate for smooth animation
}

function draw() {
  background(220);
  let t = frameCount / 60.0; // Calculate time for sinusoidal movement

  // Calculate horizontal movement for the pupils
  let moveX = sin(TWO_PI * t) * 20; // Sinusoidal movement, adjust 20 for range

  // Draw two eyes positioned symmetrically on the canvas
  drawEye(width / 3, height / 2, 80, moveX);
  drawEye(2 * width / 3, height / 2, 80, moveX);
}

function drawEye(x, y, size, moveX) {
  let irisSize = size / 2;
  let pupilSize = size / 4;

  // Calculate new iris position based on moveX
  let irisX = x + moveX;
  let irisY = y;

  // Draw sclera
  fill(255);
  ellipse(x, y, size, size);

  // Draw iris
  fill(100, 100, 200); // A blue iris
  ellipse(irisX, irisY, irisSize, irisSize);

  // Draw pupil
  fill(0);
  ellipse(irisX, irisY, pupilSize, pupilSize);
}

