  }

  img.updatePixels();
  noSmooth(); // tell p5 not to smooth the image when we scale it: we want it pixelated. This resembles Photoshop’s ‘nearest neighbor’ scaling method.
  image(img, 0, 0, width, height);
  // noLoop();
}

